mapper.select:

		select replyId, bookId, memberId, content, rating, regDate
		from psh_reply
		where bookId = #{bookId}
		order by regDate desc  		
  		limit #{skip}, #{amount}


select count(*)
from psh_reply
where bookId = #{bookId}


select avg(rating)
from psh_reply
where bookId = #{bookId}

