mapper.select:

		select distinct cateCode from psh_book where






		  							authorId = #{authorId}





  						bookName like concat ('%', #{keyword}, '%')


		select DISTINCT count(*) cateCount, a.cateCode,b.cateName from psh_book a left join psh_bcate b on a.cateCode = b.cateCode

		where 











		  							authorId = #{authorId}







  						and a.cateCode = #{cateCode}





  						bookName like concat ('%', #{keyword}, '%') and a.cateCode = #{cateCode}
